import React from "react";
import Modal from "@mui/material/Modal";
import LinearProgress from "@mui/material/LinearProgress";

const styles = {
  colorPrimary: {
    background: '#d32f2f'
  }
};

const loader = (props) => {
  return (
    <div>
      <Modal open={props.show}>
        <LinearProgress className={styles} />
      </Modal>
    </div>
  );
};

export default loader;
