import React from "react";
import Modal from "@mui/material/Modal";
import LinearProgress from "@mui/material/LinearProgress";

const loader = (props) => {
  return (
    <div>
      <Modal open={props.show}>
        <LinearProgress style={{ color: "red" }} />
      </Modal>
    </div>
  );
};

export default loader;
