import React, { useState } from 'react';
import { styled } from "@mui/material/styles";
import TextField from "@mui/material/TextField";
import InputAdornment from "@mui/material/InputAdornment";
import { Button } from '@mui/material';
import CanteenFunctionBox from '../../FunctionBox/CanteenFunctionBox/canteenFunctionBox';
import CurrencyBitcoin from '@mui/icons-material/CurrencyBitcoin';
import ShoppingCartOutlinedIcon from '@mui/icons-material/ShoppingCartOutlined';

import './canteen.scss';

const Canteen = (props) => {
  const food = {
    burger: 5,
    juice: 2,
    pizza: 15,
    samosa: 3,
    maggie: 5,
  }

  const [order, setOrder] = useState({
    foods: [],
    price: 0,
  });


  // Handlers
  const handlePriceChange = (event) => {

  };

  const handleFoodChange = (foods) => {
    setOrder({ ...order, foods: foods })
  }


  const handleSubmit = () => {

  }

  return (
    <div className='cards'>
      <div className='fbox'>
        <h1> VIRTUAL CANTEEN </h1>
        <br />
        <br />
        <div className='innerCanteen'>
          Select items to order:
          <br /> <br />
          <CanteenFunctionBox titleLeft='Stock:' titleRight='Cart:' items={Object.values(food)} handleFoodChange={handleFoodChange} />
          <div className='priceWindow'>
            <CssTextField
              sx={{ margin: "1rem", maxWidth: "300px", placeSelf: 'center' }}
              InputProps={{
                endAdornment: (
                  <InputAdornment position='end'>
                    [BHU] <CurrencyBitcoin sx={{ color: "red" }} />
                  </InputAdornment>
                ),
              }}
              id="total-price"
              name="price"
              disabled
              value={order.price}
              label="TOTAL"
            />
            <Button sx={{ maxWidth: "500px", placeSelf: 'center' }} variant='outlined' color='error' endIcon={<ShoppingCartOutlinedIcon />}
              onClick={handleSubmit} size="large">
              ORDER
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Canteen;


const CssTextField = styled(TextField)({
  '& .MuiInputBase-input': {
    color: 'white'
  },
  '& label': {
    color: '#666666'
  },
  '& label.Mui-focused': {
    color: 'white',
  },
  '& .MuiInput-underline:after': {
    borderBottomColor: '#ce3333',
  },
  '& .MuiOutlinedInput-root': {
    '& fieldset': {
      borderColor: '#666666',
    },
    '&:hover fieldset': {
      borderColor: '#ce3333',
    },
    '&.Mui-focused fieldset': {
      borderColor: '#ce3333',
    },
  },

  "& .MuiFormLabel-root.Mui-error": {
    color: "#ffdd00 !important"
  },
  '& label.Mui-focused.Mui-error': {
    color: "white !important",
  },
  '& .MuiOutlinedInput-root.Mui-error': {
    '& fieldset': {
      borderColor: "#ffdd00 !important",
    },
    '&:hover fieldset': {
      borderColor: "#ffdd00 !important",
    },
    '&.Mui-focused fieldset': {
      borderColor: "#ffdd00 !important",
    },
  },

  '& .MuiInputBase-input.Mui-disabled': {
    color: 'white'
  },
  "& .MuiFormLabel-root.Mui-disabled": {
    color: "#ce3333 !important"
  },
  '& label.Mui-focused.Mui-disabled': {
    color: "white !important",
  },
  '& .MuiOutlinedInput-root.Mui-disabled': {
    '& fieldset': {
      borderColor: "#ce3333 !important",
    },
    '&:hover fieldset': {
      borderColor: "#ce3333 !important",
    },
    '&.Mui-focused fieldset': {
      borderColor: "#ce3333 !important",
    },
  },
});
