import React, { useEffect, useState } from 'react';
import { useLocation } from "react-router-dom";
import './sections2.scss';

const Sections = (props) => {
  // Local variables
  let n = 5  // Professors per sections
  const initRenders = new Array(n).fill(false);
  let profNames = [];
  let profEmails = [];

  console.log(props.profsEmails);
  console.log(props.profsDetails);

  n = Math.min(n, props.profsEmails.length);
  for (var i; i < n; i++) {
    profNames[i] = props.profsDetails[props.profsEmails[i]].name;
    profEmails[i] = props.profsDetails[props.profsEmails[i]].email;
  }

  console.log(props.profNames);
  console.log(props.profEmails);

  // State variables
  const [renderSections, setRenderSections] = useState(initRenders);

  let { pathname } = useLocation();

  // Handlers
  const handleRenderOnClick = (idx, bool) => {
    let renders = renderSections;
    renders[idx] = bool;
    setRenderSections(renders);
  }

  useEffect(() => {
    setTimeout(() => {
      var $cont = document.querySelector('.cont');
      var $elsArr = [].slice.call(document.querySelectorAll('.el'));
      var $closeBtnsArr = [].slice.call(document.querySelectorAll('.el__close-btn'));

      setTimeout(function () {
        $cont.classList.remove('s--inactive');
      }, 200);
      console.log($elsArr)
      $elsArr.forEach(function ($el) {
        $el.addEventListener('click', function () {
          if (this.classList.contains('s--active')) return;
          $cont.classList.add('s--el-active');
          this.classList.add('s--active');
        });
      });

      $closeBtnsArr.forEach(function ($btn) {
        $btn.addEventListener('click', function (e) {
          e.stopPropagation();
          $cont.classList.remove('s--el-active');
          document.querySelector('.el.s--active').classList.remove('s--active');
        });
      });
      console.log("Homepage Rerendering...")

      // let $bg_eles = document.getElementsByClassName('el__bg')
      // for (var i = 0; i < $bg_eles.length; i++) {
      //   $bg_eles[i].style.backgroundImage = `url(https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885__480.jpg)`
      // }

    }, 2000)
  }, [pathname]);


  const SectionContent = (props) => {
    return (
      <div className="el" onClick={() => handleRenderOnClick(props.idx, true)}>
        <div className="el__overflow">
          <div className="el__inner">
            <div className="el__bg"></div>
            <div className="el__preview-cont">
              <h2 className="el__heading">{props.profName}</h2>
            </div>
            <div className="el__content">
              <div className="el__text">{props.profName}</div>
              <div className="el__close-btn" onClick={() => handleRenderOnClick(props.idx, false)}></div>
              {/*renderSections[props.idx] && <Gallery filter="leather"/>*/}
            </div>
          </div>
        </div>
        <div className="el__index">
          <div className="el__index-back">{props.profEmail}</div>
          <div className="el__index-front">
            <div className="el__index-overlay" data-index={props.profEmail}>{props.idx + 1}</div>
          </div>
        </div>
      </div>
    );
  }


  return (
    <div>
      <div className="cont s--inactive">
        {/* <!-- cont inner start --> */}
        <div className="cont__inner">
          {renderSections.map((_, idx) => <SectionContent key={idx} idx={idx} profName={profNames[idx]} profEmail={profEmails[idx]} />)};
        </div>
        {/* <!-- cont inner end --> */}
      </div>
    </div>
  );
};

export default Sections;
